// create custom plugin settings menu
add_action('admin_menu', 'cookieless_analytics_create_menu');

function cookieless_analytics_create_menu() {

	//creating the top level menu
	add_menu_page('Cookieless Analytics', 'CL Analytics', 'administrator', __FILE__, 'cookieless_analytics_settings_page' , "data:image/png;base64," );

	//call register settings function
	add_action( 'admin_init', 'register_cookieless_analytics_settings' );
}


function register_cookieless_analytics_settings() {
	//register settings
	register_setting( 'cookieless_analytics-settings-group', 'analytics_id' );
}

function cookieless_analytics_settings_page() {
	//Settings Page
	?>
	<div class="wrap">
	<h2>CookieLess Analytics</h2>

	<form method="post" action="options.php">
    	<?php settings_fields( 'cookieless_analytics-settings-group' ); ?>
    	<?php do_settings_sections( 'cookieless_analytics-settings-group' ); ?>
    	<table class="form-table">
        	<tr valign="top">
        	<th scope="row">Google Analytics ID</th>
        	<td><input type="text" name="analytics_id" value="<?php echo esc_attr( get_option('analytics_id') ); ?>" /></td>
        	</tr>
    	</table>
    	<?php submit_button(); ?>
	</form>
	</div>
	<?php
}


//make sure SESSION is set
function register_session(){
    if( !session_id() )
        session_start();
}
add_action('init','register_session');

//add our javascript to the footer
add_action( 'wp_footer', 'cookieless_analytics_javascript' ); 
function cookieless_analytics_javascript() { 
	$analyticsID=esc_attr( get_option('analytics_id') );
	if($analyticsID!=""){
		?>
		<script type="text/javascript" >
		(function(i,s,o,g,r,a,m){i['GoogleAnalyticsObject']=r;i[r]=i[r]||function(){
		(i[r].q=i[r].q||[]).push(arguments)},i[r].l=1*new Date();a=s.createElement(o),
		m=s.getElementsByTagName(o)[0];a.async=1;a.src=g;m.parentNode.insertBefore(a,m)
		})(window,document,'script','https://www.google-analytics.com/analytics.js','ga');

		jQuery(document).ready(function($) {
			//custom function to get the clientId (from the server)
			function cookieless_analytics_getId(callback){
				var data = {
				'action': 'cookieless_analytics_action',
				'mode': 'getId'
				};
				jQuery.post('/wp-admin/admin-ajax.php', data, function(response) {
					if(response!=""){
						callback(response);
					}else{
						callback(null);
					}
				});
			}

			//custom function to save the clientId to the server
			function cookieless_analytics_setId(id){
				var data = {
					'action': 'cookieless_analytics_action',
					'mode': 'saveId',
					'clientId': id
				};
				jQuery.post('/wp-admin/admin-ajax.php', data, function(response) {
				});

			}

			//getting client Id, once done start google analytics with analytics_id in settings
			cookieless_analytics_getId(function(clientId){
				ga('create', '<?php echo $analyticsID ?>', 'auto', {
    					'storage': 'none',
    					'clientId': clientId
  				});
				ga(function(tracker) {
    					cookieless_analytics_setId( tracker.get('clientId'));
  				});
				console.log("sending pageview");
				ga('send', 'pageview');
			});
		
		});
		</script>
		<?php
	}
}

//AJAX callback for the plugin
add_action( 'wp_ajax_cookieless_analytics_action', 'cookieless_analytics_ajax_callback' );
add_action( 'wp_ajax_nopriv_cookieless_analytics_action', 'cookieless_analytics_ajax_callback' );

function cookieless_analytics_ajax_callback() {
	//check if save or get 
	$mode = $_POST['mode'] ;

	if($mode =="saveId") {
		$_SESSION["COOKIELESS_ANALYTICS_CLIENTID"] = $_POST['clientId'];
	}else if($mode =="getId" && isset($_SESSION["COOKIELESS_ANALYTICS_CLIENTID"])){
		echo $_SESSION["COOKIELESS_ANALYTICS_CLIENTID"];
	}

	wp_die();
}